import React, { useEffect, useState } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios';
import Select from 'react-select';

const CarList = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [cars, setCars] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [makeFilter, setMakeFilter] = useState('');
  const [modelFilter, setModelFilter] = useState('');
  const [yearFilter, setYearFilter] = useState('');
  const [newPage, setNewPage] = useState(1);
  const pageSize = 30;

  // Options for the dropdown filter
  const filterOptions = [
    { value: 'new', label: 'New' },
    { value: 'used', label: 'Used' },
    { value: 'electric', label: 'Electric' },
    { value: 'research', label: 'Research' },
  ];

  // State to track selected option
  const [selectedFilter, setSelectedFilter] = useState(null);

  useEffect(() => {
    fetchCars();
  }, [location.search, newPage]);

  const fetchCars = () => {
    const params = new URLSearchParams(location.search);
    const searchTermParam = params.get('searchTerm') || '';
    const makeParam = params.get('make') || '';
    const modelParam = params.get('model') || '';
    const yearParam = params.get('year') || '';
    const pageParam = params.get('page') || 1;

    setSearchTerm(searchTermParam);
    setMakeFilter(makeParam);
    setModelFilter(modelParam);
    setYearFilter(yearParam);
    setNewPage(pageParam);

    const apiUrl = 'http://localhost:3001/api/cars/search';

    axios({
      method: 'post',
      url: 'http://localhost:3001/api/token',
      data: {
        grant_type: 'client_credentials',
        client_id: clientId,
        client_secret: clientSecret
      },
      headers: {
        'Content-Type': 'application/json'
      }
    })
      .then(response => {
        console.log('Token Response:', response.data);
        // Now, make the request for cars using the obtained token
        return axios.get(apiUrl, {
          params: {
            term: searchTermParam,
            make: makeParam,
            model: modelParam,
            year: yearParam,
            page: pageParam,
            pageSize: pageSize
          },
          headers: {
            Authorization: `Bearer ${response.data.access_token}`
          }
        });
      })
      .then(response => setCars(response.data))
      .catch(error => console.error('Error fetching cars:', error));
  };

  const handleSearch = () => {
    setNewPage(1);
    updateURL();
  };

  const handleFilter = (make, model, year) => {
    setNewPage(1);
    setMakeFilter(make);
    setModelFilter(model);
    setYearFilter(year);
    updateURL(1);
  };

  const handlePageChange = (newPage) => {
    setNewPage(newPage);
    updateURL();
  };

  const updateURL = (newPage) => {
    const params = new URLSearchParams();
    params.set('searchTerm', searchTerm);
    params.set('make', makeFilter);
    params.set('model', modelFilter);
    params.set('year', yearFilter);
    params.set('page', newPage);

    navigate(`?${params.toString()}`);
  };

  return (
    <div>
      {/* ... (rest of your code) */}
    </div>
  );
};

export default CarList;
